export default function Validation(values){
    const errors={};


    
    if(values.name==""){
        errors.name="Name is Required!"
    }

    if(values.age==""){
        errors.age="Age is Required!";
    }

    if(values.department==""){
        errors.department="Department is Required!"
    }

    if(values.position==""){
        errors.position="Position is Required!";
    }

    return errors;

}
